document.onreadystatechange = function () {
  if ("complete" === document.readyState) {
    var a = jQuery("select#pods-form-ui-pods-meta-translated-adapted");
    function e() {
      switch (a.val()) {
        case "translated":
          jQuery("tr.pods-form-ui-row-name-adapted-by").hide();
          jQuery("tr.pods-form-ui-row-name-translated-by").show();
          break;
        case "adapted":
          jQuery("tr.pods-form-ui-row-name-adapted-by").show();
          jQuery("tr.pods-form-ui-row-name-translated-by").hide();
          break;
        default:
          jQuery("tr.pods-form-ui-row-name-adapted-by").hide();
          jQuery("tr.pods-form-ui-row-name-translated-by").hide();
      }
    }
    e(),
      a.change(function () {
        e();
      });
  }
};
